perspectives.style:
@settings {
  template: causal-loop;
  layout: force;
  layout-preset: dense;
  connection-size: 6;
  connection-curvature: 0.28;
}

/* Correctness */
connection {
  color: scale("correctness", #FF2D00, #FF9B85);
}

/* Papers Examined */
connection {
  scale: scale("papers examined", 1, 2);
}
